function deepClone(obj = {}){
  if(typeof obj !== 'object'||obj === null){
    return obj;
  }
  //åˆå§‹åŒ–è¿”å›ç»“æœ
  let result;
  if(obj.instanceof Array){
    result = []
  }else{
    result = {}
  }
  for(let key in obj){
    if(obj.hasOwnProperty(key)){    //ä¿è¯æ˜¯è¿™ä¸ªå¯¹è±¡è‡ªå·±æ‹¥æœ‰çš„ç§æœ‰å±æ€§ï¼Œä¸æ˜¯åŸå‹çš„å±æ€§
        //è°ƒç”¨é€’å½’
       result[key] = deepClone(obj[key])
    }
  }
  return result;
}

