function Stack(){
  //ç”¨äºä¿å­˜å…ƒç´ æ•°æ®çš„æ•°ç»„
  const arr = [];

  this.push = function(element){
    arr.push(element)
  }

  this.pop = function(){
    arr.pop()
  }

  this.peek = function(){
    return arr[arr.length-1]
  }

  this.size = function(){
    return arr.length
  }

  this.isEmpty = function(){
    return arr.length === 0 ?true:false
  }  
}

export default Stack;


